(function() {
  var debug, expect, solution, util;

  solution = require("../solution");

  expect = require('chai').expect;

  util = require("util");

  debug = require("debug")("vsproj:tests:solution-test");

  describe('Solution', function() {
    it('Open Simple', function() {
      var sol;
      sol = new solution();
      return sol.open("./demo/solution.sln").then(function() {
        return expect(sol.VisualStudioVersion).to.equal("12.0.30501.0");
      });
    });
    return it('Save Simple', function() {
      var sol;
      sol = new solution();
      return sol.open("./demo/solution.sln").then(function() {
        debug("open completed", sol);
        return sol.save("./demo/solution-result.sln").then(function() {
          return expect(true).to.equal(true);
        });
      });
    });
  });

}).call(this);
